// CodewarsLink component
const CodewarsLink = ({ user }) => (
  <a href={`http://www.codewars.com/users/${user}`}>
 <br></br>
  <a href="http://www.codewars.com/users/jhoffner">jhoffner</a>
<br></br>
  <a href="http://www.codewars.com/users/GiacomoSorbi">GiacomoSorbi <br></br></a>
<br></br>
  <a href="http://www.codewars.com/users/ZozoFouchtra">ZozoFouchtra  <br></br></a>
      {user}
    </a>
  );

  // Leaderboard component
  const Leaderboard = ({ leaders }) => (
    <div>
      <h1>MY THREE FAVORITE CODEWARRIORS ARE nolan, jhoffner AND OverZealous</h1>
      <ul>
        {leaders.map((leader, index) => (
          <CodewarsLink key={index} user={leader} />
        ))}
      </ul>
    </div>
  );

  export { CodewarsLink, Leaderboard };
