import PropTypes from 'prop-types';

// CodewarsLink component
const CodewarsLink = ({ user }) => (
  <a href={`http://www.codewars.com/users/${user}`} target="_blank" rel="noopener noreferrer">
    {user}
  </a>
);

CodewarsLink.propTypes = {
  user: PropTypes.string.isRequired,
};

// Leaderboard component
const Leaderboard = ({ favoriteUsers }) => {
  const predefinedUsers = ['jhoffner', 'GiacomoSorbi', 'ZozoFouchtra'];

  return (
    <div>
      <h1>MY THREE FAVORITE CODEWARRIORS ARE nolan, jhoffner AND OverZealous</h1>
      <ul>
        {favoriteUsers.map((leader, index) => (
          <li key={index}>
            <CodewarsLink user={leader} />
          </li>
        ))}
        {predefinedUsers.map((user, index) => (
          <li key={`predefined-${index}`}>
            <CodewarsLink user={user} />
          </li>
        ))}
      </ul>
    </div>
  );
};

Leaderboard.propTypes = {
  favoriteUsers: PropTypes.arrayOf(PropTypes.string).isRequired,
};

export { CodewarsLink, Leaderboard };
